import React, { useState, useEffect } from 'react';
import { Client } from "@gradio/client";
import { ethers } from "ethers";
import { useReactMediaRecorder } from "react-media-recorder";
import './App.css';

// üåç CONFIG
const SPACE_URL = "https://toecm-pureconvo.hf.space"; 
const CONTEXTS = ["Marketplace", "School", "Traffic", "Food", "Street", "Family", "Rain", "Hospital"];

function App() {
  // --- 1. STATE ---
  const [userKey, setUserKey] = useState(null);
  const [address, setAddress] = useState("");
  const [xp, setXP] = useState(0);
  const [status, setStatus] = useState("IDLE"); 
  const [consentStatus, setConsentStatus] = useState(null);
  const [missionState, setMissionState] = useState({ 
    text: "Initializing...", emoji: "üõ∞Ô∏è", ctx: "Loading", image: "https://loremflickr.com/400/220/city" 
  });
  
  const [isReviewing, setIsReviewing] = useState(false);
  const [transcribedText, setTranscribedText] = useState("");
  const [clarificationOptions, setClarificationOptions] = useState([]);
  const [selectedClarification, setSelectedClarification] = useState("");
  const [isGeneratingOptions, setIsGeneratingOptions] = useState(false);
  const [selectedDialect, setSelectedDialect] = useState("Korean English");
  const [customDialect, setCustomDialect] = useState(""); 
  const [availableDialects, setAvailableDialects] = useState(["Korean English"]);

  const { status: recStatus, startRecording, stopRecording, mediaBlobUrl, clearBlobUrl } = useReactMediaRecorder({ 
    audio: true,
    blobPropertyBag: { type: "audio/wav" } 
  });

  // --- 2. CORE FUNCTIONS ---
  
  const fetchNewMission = React.useCallback(async (forcedContext = null) => {
    const nextContext = forcedContext || CONTEXTS[Math.floor(Math.random() * CONTEXTS.length)];
    const imageUrl = `https://loremflickr.com/400/220/${nextContext.toLowerCase()}?lock=${Date.now()}`;
    setMissionState(prev => ({ ...prev, image: imageUrl, ctx: nextContext, text: "Receiving transmissions..." }));

    try {
      const client = await Client.connect(SPACE_URL);
      const result = await client.predict("/generate_mission", [nextContext]);
      const missionData = JSON.parse(result.data[0]);
      setMissionState({ text: missionData.text, emoji: missionData.emoji, ctx: nextContext, image: imageUrl });
    } catch (e) {
      setMissionState({ text: "Describe what you see in this image.", emoji: "üì∏", ctx: nextContext, image: imageUrl });
    }
  }, []);

  // --- 3. INITIALIZATION ---
  useEffect(() => {
    const storedConsent = localStorage.getItem("pureconvo_consent");
    if (storedConsent) setConsentStatus(storedConsent);

    const initSystem = async () => {
      // Wallet / Identity
      let storedKey = localStorage.getItem("pureconvo_burner_key");
      let wallet = storedKey ? new ethers.Wallet(storedKey) : ethers.Wallet.createRandom();
      if (!storedKey) localStorage.setItem("pureconvo_burner_key", wallet.privateKey);
      
      setUserKey(wallet.privateKey);
      setAddress(wallet.address);
      setXP(parseInt(localStorage.getItem("pureconvo_xp") || "0"));

      // Get Dialects from HF
      try {
        const client = await Client.connect(SPACE_URL);
        const result = await client.predict("/get_dialects", []);
        if (result.data && Array.isArray(result.data[0])) setAvailableDialects(result.data[0]); 
      } catch (e) { console.log("Backend offline or building."); }
    };

    initSystem();
    fetchNewMission("Traffic"); 
  }, [fetchNewMission]);

  // --- 4. ACTION HANDLERS ---

  const handleReview = async () => {
    if (!mediaBlobUrl) return;
    setStatus("TRANSCRIBING...");
    try {
      const audioBlob = await fetch(mediaBlobUrl).then(r => r.blob());
      const client = await Client.connect(SPACE_URL);
      // Passing selectedDialect here helps the backend prepare the persona
      const result = await client.predict("/transcribe_check", [audioBlob, selectedDialect]);
      setTranscribedText(result.data[0]); 
      setIsReviewing(true); 
      setStatus("REVIEW");
    } catch (error) { setStatus("TRANSCRIPTION ERROR"); }
  };

  const handleGenerateClarifications = async () => {
    if (!transcribedText || isGeneratingOptions) return;
    setIsGeneratingOptions(true);
    try {
      const client = await Client.connect(SPACE_URL);
      // üî• PERSONA INJECTION: We send the selected dialect name to trigger specific AI profiles
      const result = await client.predict("/generate_clarifications", [transcribedText, selectedDialect]);
      const options = JSON.parse(result.data[0]);
      setClarificationOptions(options);
    } catch (error) {
      setClarificationOptions(["Standard Interpretation", "Contextual Dialect Phrasing"]);
    } finally {
      setIsGeneratingOptions(false);
    }
  };

  const handleFinalSubmit = async () => {
    // üõë PREVENT INFINITE LOOP / STACK OVERFLOW
    if (!selectedClarification || status === "UPLOADING") return;
    
    setStatus("UPLOADING");
    const finalDialect = selectedDialect === "ADD_NEW" ? customDialect : selectedDialect;
    
    try {
      const audioBlob = await fetch(mediaBlobUrl).then(r => r.blob());
      const client = await Client.connect(SPACE_URL);
      
      await client.predict("/check_and_submit_logic", [
        transcribedText, "+ Add New Dialect", finalDialect, selectedClarification, "Spoken", missionState.ctx, "", userKey, audioBlob, false 
      ]);

      const newXP = xp + 50;
      setXP(newXP);
      localStorage.setItem("pureconvo_xp", newXP.toString());
      
      // RESET STATE CLEANLY
      setIsReviewing(false);
      setTranscribedText("");
      setSelectedClarification("");
      setClarificationOptions([]);
      clearBlobUrl();
      setStatus("‚úÖ SAVED TO PURE CHAIN");
      
      setTimeout(() => {
        setStatus("IDLE");
        fetchNewMission();
      }, 2000);

    } catch (error) {
      console.error(error);
      setStatus("UPLOAD ERROR");
    }
  };

  // --- 5. RENDER ---
  if (!consentStatus) return (
    <div className="game-wrapper" style={{display:'flex', alignItems:'center', justifyContent:'center', height:'100vh', background:'#020617'}}>
      <div style={{background:'#0f172a', padding:'40px', borderRadius:'20px', border:'1px solid #3b82f6', textAlign:'center', color:'white', maxWidth:'450px'}}>
        <h1 style={{color:'#60a5fa'}}>üåç PureConvo</h1>
        <p>Help train AI to understand your community. Your dialect contributions are secured via Pure Chain blockchain.</p>
        <button className="cyber-button SUCCESS" onClick={() => {localStorage.setItem("pureconvo_consent", "yes"); setConsentStatus("yes")}}>I AGREE / START</button>
      </div>
    </div>
  );

  return (
    <div className="game-wrapper">
      <div className="game-container">
        {/* HUD */}
        <div className="hud-header">
          <div className="level-badge">LVL {calculateLevel(xp)}</div>
          <div className="xp-bar-container"><div className="xp-bar-fill" style={{width: `${getProgress(xp)}%`}}></div></div>
        </div>

        {/* MISSION CARD */}
        <div className="mission-card">
          <img src={missionState.image} alt="Target" style={{width:'100%', height:'180px', objectFit:'cover'}} />
          <div style={{padding:'20px'}}>
            <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                <span style={{color:'#60a5fa', fontWeight:'bold'}}>{missionState.emoji} {missionState.ctx}</span>
                <button onClick={() => fetchNewMission()} style={{background:'none', border:'1px solid #334155', color:'#64748b', cursor:'pointer', padding:'2px 8px', borderRadius:'5px'}}>SKIP</button>
            </div>
            <p style={{color:'white', fontSize:'18px', marginTop:'10px'}}>"{missionState.text}"</p>
          </div>
        </div>

        {/* CONTROLS */}
        <div className="controls-area">
          <p style={{fontSize:'12px', color:'#94a3b8', marginBottom:'5px'}}>Choose your English dialect:</p>
          <select className="cyber-select" value={selectedDialect} onChange={e => setSelectedDialect(e.target.value)}>
            {availableDialects.map(d => <option key={d} value={d}>{d}</option>)}
          </select>
          
          {isReviewing ? (
            <div className="review-box">
              <textarea className="cyber-input" value={transcribedText} onChange={e => setTranscribedText(e.target.value)} />
              
              {!clarificationOptions.length ? (
                <button className="cyber-button" onClick={handleGenerateClarifications} disabled={isGeneratingOptions}>
                  {isGeneratingOptions ? "üß† ACTIVATING PERSONA..." : "GET INTERPRETATIONS"}
                </button>
              ) : (
                <div className="clarification-area">
                  <p style={{fontSize:'11px', color:'#60a5fa', marginBottom:'10px'}}>AI Interpretation Results:</p>
                  {clarificationOptions.map((opt, i) => (
                    <div key={i} onClick={() => setSelectedClarification(opt)} className={`opt ${selectedClarification === opt ? 'active' : ''}`}>{opt}</div>
                  ))}
                  <button className="cyber-button SUCCESS" onClick={handleFinalSubmit} disabled={!selectedClarification || status === "UPLOADING"}>
                    {status === "UPLOADING" ? "CONNECTING TO CHAIN..." : "SUBMIT TO BLOCKCHAIN"}
                  </button>
                </div>
              )}
              <button onClick={() => {setIsReviewing(false); setClarificationOptions([]);}} className="discard-btn">CANCEL</button>
            </div>
          ) : (
            <>
              <div className="record-area" style={{textAlign:'center'}}>
                <button className={`record-btn ${recStatus === "recording" ? "recording" : ""}`} onMouseDown={startRecording} onMouseUp={stopRecording}>
                  {recStatus === "recording" ? "üéôÔ∏è RECORDING..." : "üéôÔ∏è HOLD TO SPEAK"}
                </button>
              </div>

              {mediaBlobUrl && (
                <div style={{margin:'15px 0', background:'rgba(30, 41, 59, 0.5)', padding:'10px', borderRadius:'10px', border:'1px solid #334155'}}>
                  <audio src={mediaBlobUrl} controls style={{width:'100%', height:'30px'}} />
                </div>
              )}

              <button className="cyber-button" onClick={handleReview} disabled={!mediaBlobUrl || status !== "IDLE"}>
                {status === "IDLE" ? "ANALYZE VOICE" : status}
              </button>
            </>
          )}
          <div className="id-footer">OPERATOR: {address.slice(0,6)}... | {selectedDialect.toUpperCase()}</div>
        </div>
      </div>
    </div>
  );
}

export default App;
