import { v4 as uuidv4 } from 'uuid';
import React, { useState, useEffect } from 'react';
import { Client } from "@gradio/client";
import { useReactMediaRecorder } from "react-media-recorder";
import './App.css';

const SPACE_URL = "https://toecm-pureconvo.hf.space"; 
const FALLBACK_DIALECTS = ["Nigerian English", "Naija Pidgin", "Indian English", "American English", "British English"]; 
const TONES = ["Neutral / Conversational", "Casual / Slang", "Formal / Professional", "Proverb / Idiom"];

// üü¢ NEW: Split Greetings for Context
const NEW_GREETINGS = [
    "Hello!", "Hi there!", "Greetings!", "Ready?", "Salutations!", "Welcome!"
];

const RETURNING_GREETINGS = [
    "Welcome back!", "Good to see you again!", "Ready for another round?", 
    "Hello again!", "Back for more?", "Resuming session!"
];

const PRIVACY_STATEMENTS = [
    // Data Sovereignty
    "Rest easy‚Äîwe collect sounds, not names.",
    "Your voice is a contribution; your identity is your own.",
    "Encrypted, anonymous, and essential.",
    "No profiles, no tracking. Just your unique voice.",
    "We‚Äôre listening to the data, not the person. You‚Äôre safe here.",
    // Contribution & Impact
    "Powering the future of linguistics, one anonymous clip at a time.",
    "Your English is a treasure for science. Thanks for sharing!",
    "Every syllable helps! Keeping your data private, always.",
    "Join the global chorus. 100% private. 100% impactful.",
    "Small voice, big data, zero footprints. Let‚Äôs begin.",
    // Short & Friendly
    "Speak your mind. We‚Äôll keep the secret.",
    "Private by design. Scientific by nature.",
    "Your identity: Hidden. Your impact: Huge.",
    "Safe. Secure. Science-forward.",
    "Authentic voices. Anonymous data."
];

// Helper to pick random item
const getRandom = (arr) => arr[Math.floor(Math.random() * arr.length)];

// Doodle Fetcher
const getDoodleUrl = (keyword) => {
    return `https://loremflickr.com/400/200/${keyword},sketch,drawing/all`;
};

function App() {
  // --- STATE ---
  const [isPulsing, setIsPulsing] = useState(false);
  const [userKey, setUserKey] = useState(null);
  const [address, setAddress] = useState("");
  const [xp, setXP] = useState(0);
  const [status, setStatus] = useState("IDLE"); 
  const [consentStatus, setConsentStatus] = useState(null);

  // üü¢ UPDATED: Smart Greeting Logic
  const [missionState, setMissionState] = useState(() => {
    // Check local storage synchronously to set initial state
    const hasKey = localStorage.getItem("pure_user_key");
    const greetingList = hasKey ? RETURNING_GREETINGS : NEW_GREETINGS;
    
    return { 
        text: `${getRandom(greetingList)} ${getRandom(PRIVACY_STATEMENTS)}`, 
        emoji: "üëã", 
        ctx: "Loading", 
        image: "" 
    };
  });
  
  const [isReviewing, setIsReviewing] = useState(false);
  const [transcribedText, setTranscribedText] = useState("");
  const [dialects, setDialects] = useState([]);
  const [selectedDialect, setSelectedDialect] = useState("");
  const [selectedTone, setSelectedTone] = useState("Neutral / Conversational");
  const [clarification, setClarification] = useState("");
  const [context, setContext] = useState("");
  const [pragmatics, setPragmatics] = useState("");

  const { status: recStatus, startRecording, stopRecording, mediaBlobUrl } = useReactMediaRecorder({ audio: true });

  // --- INIT ---
  useEffect(() => {
    // 1. Mic Warmup
    navigator.mediaDevices.getUserMedia({ audio: true })
      .then(stream => {
        console.log("Mic Warmed Up");
        stream.getTracks().forEach(track => track.stop());
      })
      .catch(e => console.log("Mic Permission Pending...", e));

    // 2. ID Generation (Runs AFTER the initial state check)
    const storedKey = localStorage.getItem("pure_user_key");
    if (storedKey) {
      setUserKey(storedKey);
      setAddress("0x" + storedKey.replace(/-/g, "").slice(0, 40)); 
    } else {
      const newKey = uuidv4();
      localStorage.setItem("pure_user_key", newKey);
      setUserKey(newKey);
      setAddress("0x" + newKey.replace(/-/g, "").slice(0, 40));
    }

    // 3. Fetch Dialects
    const fetchDialects = async () => {
      try {
        const app = await Client.connect(SPACE_URL);
        const result = await app.predict("/get_dialects"); 
        
        if (result?.data?.[0]) {
            const dList = JSON.parse(result.data[0]);
            setDialects(dList);
            const savedDialect = localStorage.getItem("pure_saved_dialect");
            if (savedDialect && dList.includes(savedDialect)) {
                setSelectedDialect(savedDialect);
            } else if (dList.length > 0) {
                setSelectedDialect(dList[0]);
            }
        }
      } catch (e) { 
        setDialects(FALLBACK_DIALECTS);
        const savedDialect = localStorage.getItem("pure_saved_dialect");
        if (savedDialect && FALLBACK_DIALECTS.includes(savedDialect)) {
             setSelectedDialect(savedDialect);
        } else {
             setSelectedDialect(FALLBACK_DIALECTS[0]);
        }
      }
    };
    fetchDialects();

    // 4. Fetch Mission & Image
    const fetchMission = async () => {
        try {
            const app = await Client.connect(SPACE_URL);
            const result = await app.predict("/generate_mission", [ "General" ]);
            const data = JSON.parse(result.data[0]);
            
            const keyword = data.text.split(" ").slice(-1)[0].replace(/[^a-zA-Z]/g, "") || "abstract";
            
            setMissionState({
                // We keep the initial greeting text!
                text: data.text || "Describe your day.", 
                emoji: data.emoji || "üå§Ô∏è",
                ctx: "Daily Life",
                image: getDoodleUrl(keyword) 
            });
        } catch (e) {
             setMissionState(prev => ({ 
                ...prev,
                text: "Describe your morning routine.", 
                emoji: "‚òï", 
                ctx: "Daily Life",
                image: getDoodleUrl("coffee")
            }));
        }
    };
    fetchMission();
  }, []);

  // --- HANDLERS ---
  const handleDialectChange = (e) => {
      const newVal = e.target.value;
      setSelectedDialect(newVal);
      localStorage.setItem("pure_saved_dialect", newVal);
  };

  const handleReview = async () => {
    if (!mediaBlobUrl) return;
    setStatus("ANALYZING...");
    
    try {
        const blob = await fetch(mediaBlobUrl).then(r => r.blob());
        const app = await Client.connect(SPACE_URL);
        
        const transResult = await app.predict("/transcribe_check", [ blob, selectedDialect ]);
        setTranscribedText(transResult.data[0]);

        const clarifyResult = await app.predict("/generate_clarifications", [ transResult.data[0], selectedDialect ]);
        const analysisData = JSON.parse(clarifyResult.data[0]);
        
        setClarification(analysisData.clarification || analysisData.Meaning || "");
        setContext(analysisData.context || "General");
        setPragmatics(analysisData.pragmatics || "");
        
        setIsReviewing(true);
        setStatus("IDLE");
    } catch (e) {
        setTranscribedText("Error analyzing audio. Please type manually.");
        setIsReviewing(true);
        setStatus("IDLE");
    }
  };

  const handleSubmit = async () => {
    setStatus("MINTING...");
    try {
        const blob = await fetch(mediaBlobUrl).then(r => r.blob());
        const app = await Client.connect(SPACE_URL);
        await app.predict("/check_and_submit_logic", [
            transcribedText, selectedDialect, "", clarification, selectedTone, context, pragmatics, userKey, blob, false
        ]);
        setXP(prev => prev + 50);
        setStatus("SUCCESS! +50 XP");
        setTimeout(() => {
            setIsReviewing(false);
            setTranscribedText("");
            setStatus("IDLE");
            // Refresh Greeting on success (Simulate "Next Round")
            setMissionState(prev => ({
                ...prev,
                text: `${getRandom(RETURNING_GREETINGS)} ${getRandom(PRIVACY_STATEMENTS)}`
            }));
        }, 2000);
    } catch (e) {
        setStatus("ERROR");
    }
  };

  const copyIDToClipboard = () => {
     if (userKey) {
         navigator.clipboard.writeText(userKey);
         setIsPulsing(true);
         setTimeout(() => setIsPulsing(false), 500);
         alert("Session ID Copied: " + userKey);
     }
  };

  if (!consentStatus) {
    return (
        <div className="consent-screen">
            <h1>üîê Data Consent</h1>
            <p>We collect voice samples to build an open-source dialect dataset.</p>
            <button className="cyber-button" onClick={() => setConsentStatus(true)}>I AGREE</button>
        </div>
    );
  }

  return (
    <div className="App">
      <div className="cyber-container">
        {/* HEADER */}
        <div className="hud-header">
            <div className="xp-badge">‚ú® {xp} XP</div>
            <div className="status-badge">{status}</div>
        </div>

        {/* MISSION CARD (With Doodle) */}
        <div className="mission-card">
            <div 
                className="doodle-bg" 
                style={{backgroundImage: `url(${missionState.image})`}} 
            />
            <div className="mission-content-overlay">
                <div className="mission-icon">{missionState.emoji}</div>
                <div>
                    <h3>CURRENT OBJECTIVE</h3>
                    <p>{missionState.text}</p>
                </div>
            </div>
        </div>

        {/* CONTROLS */}
        <div className="control-panel">
            {!isReviewing ? (
                <>
                    <div className="dialect-selector">
                        <label>TARGET DIALECT</label>
                        <select value={selectedDialect} onChange={handleDialectChange}>
                            {dialects.map(d => <option key={d} value={d}>{d}</option>)}
                        </select>
                    </div>

                    <div className={`record-zone ${recStatus === "recording" ? "active" : ""}`}>
                        <button 
                            className={`record-btn ${recStatus === "recording" ? "pulsing" : ""}`}
                            onMouseDown={startRecording} onMouseUp={stopRecording}
                            onTouchStart={startRecording} onTouchEnd={stopRecording}
                        >
                            {recStatus === "recording" ? "üéôÔ∏è RECORDING..." : "üéôÔ∏è HOLD TO SPEAK"}
                        </button>
                    </div>

                    {mediaBlobUrl && (
                        <div className="audio-preview">
                            <audio src={mediaBlobUrl} controls />
                        </div>
                    )}

                    <button className="cyber-button" onClick={handleReview} disabled={!mediaBlobUrl || status !== "IDLE"}>
                        {status === "IDLE" ? "ANALYZE VOICE" : status}
                    </button>
                </>
            ) : (
                <>
                    {/* REVIEW PHASE */}
                    <div className="audio-preview">
                        <label style={{fontSize:'10px', color:'#94a3b8'}}>REVIEW AUDIO:</label>
                        <audio src={mediaBlobUrl} controls />
                    </div>

                    <div className="input-group">
                        <label>I HEARD:</label>
                        <textarea value={transcribedText} onChange={(e) => setTranscribedText(e.target.value)} />
                    </div>

                    <div className="input-group">
                        <label>MEANING (Standard English):</label>
                        <textarea value={clarification} onChange={(e) => setClarification(e.target.value)} />
                    </div>

                    <div className="input-group">
                        <label>TONE:</label>
                        <select value={selectedTone} onChange={(e) => setSelectedTone(e.target.value)}>
                            {TONES.map(t => <option key={t} value={t}>{t}</option>)}
                        </select>
                    </div>

                    <div className="action-row">
                        <button className="cancel-btn" onClick={() => setIsReviewing(false)}>BACK</button>
                        <button className="cyber-button" onClick={handleSubmit}>MINT (+50 XP)</button>
                    </div>
                </>
            )}
        </div>

        {/* FOOTER */}
        <div className={`id-footer ${isPulsing ? 'pulse-active' : ''}`} onClick={copyIDToClipboard} title="Click to copy">
            <div>OPERATOR: {address.slice(0, 6)}...{address.slice(-4)}</div>
            <div style={{fontSize: '0.8em', opacity: 0.6}}>ID: {userKey ? `${userKey.slice(0, 8)}...` : "Loading..."} üìã</div>
        </div>

      </div>
    </div>
  );
}

export default App;
