import React, { useState, useEffect } from 'react';
import { Client } from "@gradio/client";
import { ethers } from "ethers";
import { useReactMediaRecorder } from "react-media-recorder";
import './App.css';

// ðŸ”´ CONFIG
const SPACE_URL = "http://localhost:7860"; 

// --- GAME LOGIC UTILS ---
const calculateLevel = (xp) => Math.floor(xp / 100) + 1;
const getProgress = (xp) => xp % 100; 
const CONTEXTS = ["Marketplace", "School", "Traffic", "Food", "Street", "Family", "Rain", "Hospital"];

function App() {
  // --- STATE ---
  const [userKey, setUserKey] = useState(null);
  const [address, setAddress] = useState("");
  const [xp, setXP] = useState(0);
  const [streak] = useState(1);
  const [status, setStatus] = useState("IDLE"); 

  // ETHICAL CONSENT STATE
  const [consentStatus, setConsentStatus] = useState(null);

  // ANTI-DUPLICATE / TYPO STATE
  const [ambiguousMatch, setAmbiguousMatch] = useState(null);

  // CAPTCHA STATE
  const [showCaptcha, setShowCaptcha] = useState(false);
  const [captchaCode, setCaptchaCode] = useState("");
  const [captchaStatus, setCaptchaStatus] = useState("IDLE"); // IDLE, RECORDING, VERIFYING, SUCCESS

  // Mission State
  const [missionState, setMissionState] = useState({ 
    text: "Initializing Satellite Feed...", 
    emoji: "ðŸ›°ï¸", 
    ctx: "Loading",
    image: "https://loremflickr.com/400/220/city" 
  });

  // Transcription & Editing State
  const [isReviewing, setIsReviewing] = useState(false);
  const [transcribedText, setTranscribedText] = useState("");
  const [clarificationOptions, setClarificationOptions] = useState([]);
  const [selectedClarification, setSelectedClarification] = useState("");
  const [isGeneratingOptions, setIsGeneratingOptions] = useState(false);

  // Form Inputs
  const [selectedDialect, setSelectedDialect] = useState("Korean English");
  const [customDialect, setCustomDialect] = useState(""); 
  const [availableDialects, setAvailableDialects] = useState(["Korean English"]);

  const { status: recStatus, startRecording, stopRecording, mediaBlobUrl, clearBlobUrl } = useReactMediaRecorder({ 
    audio: true,
    blobPropertyBag: { type: "audio/wav" } 
  });

  // --- 1. INIT & CONSENT CHECK ---
  useEffect(() => {
    const storedConsent = localStorage.getItem("pureconvo_consent");
    if (storedConsent) {
      setConsentStatus(storedConsent);
    }

    const initIdentity = () => {
      let storedKey = localStorage.getItem("pureconvo_burner_key");
      let storedXP = parseInt(localStorage.getItem("pureconvo_xp") || "0");
      let wallet;
      if (!storedKey) {
        wallet = ethers.Wallet.createRandom();
        storedKey = wallet.privateKey;
        localStorage.setItem("pureconvo_burner_key", storedKey);
      } else {
        wallet = new ethers.Wallet(storedKey);
      }
      setUserKey(storedKey);
      setAddress(wallet.address);
      setXP(storedXP);
    };

    const fetchDialects = async () => {
      try {
        const client = await Client.connect(SPACE_URL);
        const result = await client.predict("/get_dialects", []);
        if (result.data && Array.isArray(result.data[0])) setAvailableDialects(result.data[0]); 
      } catch (e) { console.log("Offline Mode"); }
    };

    initIdentity();
    fetchDialects();
    fetchNewMission("Traffic"); 
  }, [fetchNewMission]); // Now fetchNewMission is a stable dependency

  // --- MISSION GENERATOR ---
  // Wrap in useCallback to satisfy the dependency rule
  const fetchNewMission = React.useCallback(async (forcedContext = null) => {
    const nextContext = forcedContext || CONTEXTS[Math.floor(Math.random() * CONTEXTS.length)];
    const imageUrl = `https://loremflickr.com/400/220/${nextContext.toLowerCase()}?lock=${Date.now()}`;
    setMissionState(prev => ({ ...prev, image: imageUrl, ctx: nextContext, text: "Receiving transmissions..." }));

    if (consentStatus === "offline") {
       setMissionState({ text: "Describe this scene (Offline Practice).", emoji: "ðŸ“µ", ctx: nextContext, image: imageUrl });
       return;
    }

    try {
      const client = await Client.connect(SPACE_URL);
      const result = await client.predict("/generate_mission", [nextContext]);
      const missionData = JSON.parse(result.data[0]);
      setMissionState({
        text: missionData.text,
        emoji: missionData.emoji,
        ctx: nextContext,
        image: imageUrl 
      });
    } catch (e) {
      setMissionState({ text: "Describe what you see in this image.", emoji: "ðŸ“¸", ctx: nextContext, image: imageUrl });
    }
  }, [consentStatus]);
  
  // --- CONSENT HANDLERS ---
  const handleConsentAgree = () => {
    localStorage.setItem("pureconvo_consent", "contribute");
    setConsentStatus("contribute");
  };

  const handleConsentDecline = () => {
    localStorage.setItem("pureconvo_consent", "offline");
    setConsentStatus("offline");
  };

  // --- CAPTCHA LOGIC (Fixed) ---
  const initCaptcha = () => {
    // Generate random 4 digits
    const code = Math.floor(1000 + Math.random() * 9000).toString();
    const formatted = code.split('').join('-'); // "4-2-9-1"
    setCaptchaCode(formatted);
    setShowCaptcha(true);
    setCaptchaStatus("IDLE");
  };

  const verifyCaptcha = async () => {
    if (!mediaBlobUrl) return;
    setCaptchaStatus("VERIFYING");
    
    try {
      const audioBlob = await fetch(mediaBlobUrl).then(r => r.blob());
      const client = await Client.connect(SPACE_URL);
      
      const result = await client.predict("/verify_captcha", [audioBlob, captchaCode]);
      const response = JSON.parse(result.data[0]);
      
      if (response.success) {
        setCaptchaStatus("SUCCESS");
        setTimeout(() => {
            setShowCaptcha(false); // Close modal
            handleFinalSubmit(false); // Proceed to actual submit
        }, 1000);
      } else {
        setCaptchaStatus("FAILED");
        clearBlobUrl(); // Reset for retry
      }
    } catch (e) {
      console.error(e);
      setCaptchaStatus("ERROR");
    }
  };

  // --- REVIEW & SUBMIT HANDLERS ---
  const handleReview = async () => {
    if (!mediaBlobUrl) return;
    setStatus("TRANSCRIBING...");
    
    if (consentStatus === "offline") {
      setTranscribedText("Offline Mode: Transcription Unavailable.");
      setIsReviewing(true);
      setStatus("OFFLINE");
      return;
    }

    try {
      const audioBlob = await fetch(mediaBlobUrl).then(r => r.blob());
      const client = await Client.connect(SPACE_URL);
      const result = await client.predict("/transcribe_check", [audioBlob]);
      setTranscribedText(result.data[0]); 
      setClarificationOptions([]); 
      setSelectedClarification("");
      setIsReviewing(true); 
      setStatus("REVIEW");
    } catch (error) {
      console.error(error);
      setStatus("ERROR");
    }
  };

  const handleGenerateClarifications = async () => {
    if (!transcribedText) return;
    setIsGeneratingOptions(true);
    
    if (consentStatus === "offline") {
        setClarificationOptions(["Offline Option A", "Offline Option B"]);
        setIsGeneratingOptions(false);
        return;
    }

    try {
      const client = await Client.connect(SPACE_URL);
      const result = await client.predict("/generate_clarifications", [transcribedText]);
      const options = JSON.parse(result.data[0]);
      setClarificationOptions(options);
    } catch (error) {
      setClarificationOptions(["Literal Translation", "Standard English Paraphrase"]);
    }
    setIsGeneratingOptions(false);
  };

  // --- 5. HANDLE FINAL SUBMIT ---
  const handleFinalSubmit = async (forceNew = false) => {
    if (!selectedClarification) return; 
    
    // OFFLINE CHECK
    if (consentStatus === "offline") {
      alert("You are in Offline/No-Contribution Mode. Data was NOT sent.");
      setIsReviewing(false);
      clearBlobUrl();
      return;
    }

    // CAPTCHA CHECK
    if (selectedDialect === "ADD_NEW" && captchaStatus !== "SUCCESS" && !forceNew) {
        initCaptcha(); 
        return;       
    }

    setStatus("UPLOADING");
    const finalDialectName = selectedDialect === "ADD_NEW" ? customDialect : selectedDialect;
    
    try {
      const audioBlob = await fetch(mediaBlobUrl).then(r => r.blob());
      const client = await Client.connect(SPACE_URL);
      
      const result = await client.predict("/check_and_submit_logic", [
        transcribedText, 
        "+ Add New Dialect", 
        finalDialectName, 
        selectedClarification, 
        "Spoken", 
        missionState.ctx, 
        "", 
        userKey, 
        audioBlob,
        forceNew 
      ]);

      const serverMessage = result.data[0]; 

      // CHECK FOR AMBIGUITY
      if (serverMessage.includes("âš ï¸ AMBIGUOUS:")) {
          const suggestion = serverMessage.split(":")[1];
          setAmbiguousMatch(suggestion);
          setStatus("WAITING"); 
          return; 
      }

      setStatus(serverMessage);
      const newXP = xp + 50 + (streak * 5);
      setXP(newXP);
      localStorage.setItem("pureconvo_xp", newXP.toString());
      
      setIsReviewing(false); setTranscribedText(""); setClarificationOptions([]); 
      setSelectedClarification(""); setAmbiguousMatch(null); clearBlobUrl(); 
      fetchNewMission(); 
      
      setTimeout(() => setStatus("IDLE"), 4000);

    } catch (error) {
      console.error(error);
      setStatus("ERROR: " + error.message);
    }
  };

  // --- NEW: GENERATE PERSONA (Admin) ---
  const handleGeneratePersona = async () => {
    setStatus("GENERATING PROFILE...");
    try {
      const client = await Client.connect(SPACE_URL);
      const result = await client.predict("/generate_persona_from_csv", [selectedDialect]);
      const response = JSON.parse(result.data[0]);
      setStatus(response.status === "success" ? "âœ… PROFILE CREATED!" : "âŒ " + response.message);
      setTimeout(() => setStatus("IDLE"), 3000);
    } catch (e) { setStatus("ERROR"); }
  };

  // --- RENDER: CONSENT SCREEN ---
  if (consentStatus === null) {
    return (
      <div className="game-wrapper" style={{display:'flex', alignItems:'center', justifyContent:'center'}}>
        <div style={{
            background: '#0f172a', border: '1px solid #3b82f6', borderRadius: '20px', 
            padding: '40px', maxWidth: '500px', color: 'white', textAlign: 'center',
            boxShadow: '0 0 50px rgba(59, 130, 246, 0.3)'
        }}>
          <div style={{fontSize: '50px', marginBottom: '20px'}}>ðŸŒ</div>
          <h1 style={{fontSize: '24px', fontWeight: 'bold', marginBottom: '15px', color: '#60a5fa'}}>
            Play a Game, Help Your Community.
          </h1>
          <p style={{lineHeight: '1.6', color: '#cbd5e1', marginBottom: '30px'}}>
            This game is part of a community science project. By playing, you are helping train an AI to recognize <strong>your community's English</strong> and other dialects in our neighborhood. 
            <br/><br/>
Your moves help build a public dataset for everyone to use. We do NOT record your location or private conversations. Pure Chain (a blockchain) technology has been implemented in this system for advanced, specialized encryption to secure data and transactions. 

          </p>
          <div style={{display: 'flex', flexDirection: 'column', gap: '15px'}}>
            <button onClick={handleConsentAgree} className="cyber-button SUCCESS" style={{padding: '15px', fontSize: '16px'}}>âœ… I AGREE / START CONTRIBUTING</button>
            <button onClick={handleConsentDecline} style={{background: 'transparent', border: '1px solid #64748b', color: '#94a3b8', padding: '12px', borderRadius: '10px', cursor: 'pointer'}}>Play Offline / Without Contributing</button>
          </div>
        </div>
      </div>
    );
  }

  // --- RENDER: MAIN GAME ---
  const level = calculateLevel(xp);
  const progress = getProgress(xp);

  return (
    <div className="game-wrapper">
      <div className="game-container">
        
        {/* HUD */}
        <div className="hud-header">
          <div className="level-badge"><span className="lvl-label">LVL</span><span className="lvl-val">{level}</span></div>
          <div className="xp-bar-container"><div className="xp-bar-fill" style={{width: `${progress}%`}}></div></div>
        </div>

        {/* MISSION CARD */}
        <div className="mission-card" style={{ 
            display: 'flex', flexDirection: 'column', borderRadius: '20px',
            overflow: 'hidden', border: '1px solid rgba(255, 255, 255, 0.2)',
            boxShadow: '0 10px 30px rgba(0,0,0,0.5)', backgroundColor: '#0f172a',
            position: 'relative'
        }}>
          <img src={missionState.image} alt="Mission" style={{ width: '100%', height: '180px', objectFit: 'cover' }} />
          <div className="mission-content" style={{ padding: '20px' }}>
            <div className="mission-header" style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
              <div><span className="mission-emoji">{missionState.emoji}</span><span className="mission-ctx">TARGET: {missionState.ctx.toUpperCase()}</span></div>
              <button onClick={handleSkip} style={{ background: 'transparent', border: '1px solid rgba(255,255,255,0.3)', color: '#94a3b8', borderRadius: '20px', padding: '4px 10px', fontSize: '10px', fontWeight: 'bold', cursor: 'pointer', letterSpacing: '1px' }}>SKIP â­ï¸</button>
            </div>
            <div className="mission-target" style={{marginTop: '10px', marginBottom: '10px'}}>"{missionState.text}"</div>
            <div className="mission-sub">Please choose your English/dialect below. If you can't find one for your community or country, add it to the list using the format, "YouCommunity English", e.g., "Japanese English" or â€œAfricanAmerican Englishâ€. After that, hold the record button to describe the first thing you see in one sentence.</div>
          </div>
        </div>

        {/* CONTROLS */}
        <div className="controls-area">
          <div className="control-row">
            <select className="cyber-select" value={selectedDialect} onChange={e => setSelectedDialect(e.target.value)}>
              {availableDialects.map(d => <option key={d} value={d}>{d}</option>)}
              <option value="ADD_NEW">âž• Add "[YourCommunity] English"...</option>
            </select>
          </div>
          {selectedDialect === "ADD_NEW" && (
            <input type="text" className="cyber-input" placeholder="Name your Dialect..." value={customDialect} onChange={e => setCustomDialect(e.target.value)} />
          )}
          
          {isReviewing ? (
            <div className="review-box" style={{animation: 'fadeIn 0.5s', background: 'rgba(0,0,0,0.3)', padding: '15px', borderRadius: '15px', marginTop: '10px'}}>
              <div style={{color: '#94a3b8', fontSize: '12px', marginBottom: '5px', fontWeight: 'bold'}}>STEP 1: EDIT TEXT</div>
              <textarea className="cyber-input" value={transcribedText} onChange={(e) => setTranscribedText(e.target.value)} style={{minHeight: '80px', fontSize: '16px', color: 'white', marginBottom: '10px', width: '90%'}}/>

              {clarificationOptions.length === 0 ? (
                <button className="cyber-button" onClick={handleGenerateClarifications} disabled={isGeneratingOptions} style={{background: 'linear-gradient(135deg, #3b82f6, #2563eb)', width: '100%', padding: '12px', borderRadius: '10px', border: 'none', color: 'white', fontWeight: 'bold', cursor: 'pointer'}}>
                  {isGeneratingOptions ? "ðŸ§  GENERATING OPTIONS..." : "â¬‡ï¸ NEXT: GET CLARIFICATIONS"}
                </button>
              ) : (
                <div className="clarification-area">
                  <div style={{color: '#94a3b8', fontSize: '12px', marginBottom: '5px', marginTop: '10px', fontWeight: 'bold'}}>STEP 2: CHOOSE MEANING</div>
                  {clarificationOptions.map((opt, idx) => (
                    <div key={idx} onClick={() => setSelectedClarification(opt)} style={{padding: '12px', marginBottom: '8px', borderRadius: '10px', cursor: 'pointer', fontSize: '14px', color: selectedClarification === opt ? 'white' : '#cbd5e1', border: selectedClarification === opt ? '1px solid #10b981' : '1px solid rgba(148, 163, 184, 0.2)', background: selectedClarification === opt ? 'rgba(16, 185, 129, 0.2)' : 'rgba(30, 41, 59, 0.5)', fontWeight: selectedClarification === opt ? 'bold' : 'normal'}}>
                      {opt}
                    </div>
                  ))}
                  <button className="cyber-button" onClick={handleFinalSubmit} disabled={status === "UPLOADING" || !selectedClarification} style={{marginTop: '15px', width: '100%', background: selectedClarification ? 'linear-gradient(135deg, #059669, #10b981)' : '#334155', padding: '12px', borderRadius: '10px', border: 'none', color: 'white', fontWeight: 'bold', cursor: selectedClarification ? 'pointer' : 'not-allowed'}}>
                    {status === "UPLOADING" ? "SAVING..." : "âœ… SUBMIT FOR APPROVAL"}
                  </button>
                </div>
              )}
              
              {/* AMBIGUOUS MATCH CONFIRMATION UI */}
              {ambiguousMatch && (
                <div style={{background: '#334155', padding: '15px', borderRadius: '10px', marginTop: '10px', border: '1px solid #f59e0b'}}>
                  <div style={{color: '#fcd34d', fontWeight: 'bold', marginBottom: '10px', fontSize: '13px'}}>âš ï¸ Similar Dialect Found</div>
                  <div style={{color: 'white', fontSize: '14px', marginBottom: '15px'}}>
                    Did you mean <strong>"{ambiguousMatch}"</strong>?<br/>
                    <span style={{fontSize:'12px', color:'#94a3b8'}}>Or create "{customDialect}" as a new dialect?</span>
                  </div>
                  <button onClick={() => { setCustomDialect(ambiguousMatch); setAmbiguousMatch(null); setTimeout(() => handleFinalSubmit(false), 100); }} className="cyber-button" style={{background: '#10b981', marginBottom: '8px'}}>Use Existing: "{ambiguousMatch}"</button>
                  <button onClick={() => { setAmbiguousMatch(null); handleFinalSubmit(true); }} className="cyber-button" style={{background: '#64748b'}}>Create New: "{customDialect}"</button>
                </div>
              )}

              <button onClick={() => { setIsReviewing(false); setTranscribedText(""); setClarificationOptions([]); }} style={{background:'none', border:'none', color:'#ef4444', marginTop:'10px', cursor:'pointer', width:'100%', fontWeight: 'bold'}}>âŒ DISCARD & RETRY</button>
            </div>
          ) : (
            <>
              <div className="record-area">
                 {recStatus === "recording" ? (
                   <button className="record-btn recording" onMouseUp={stopRecording} onTouchEnd={stopRecording}><div className="pulse-ring"></div>ðŸŽ™ï¸ RELEASE</button>
                 ) : (
                   <button className="record-btn" onMouseDown={startRecording} onTouchStart={startRecording}>ðŸŽ™ï¸ HOLD TO SPEAK</button>
                 )}
              </div>
              {mediaBlobUrl && (<div className="audio-preview"><audio src={mediaBlobUrl} controls /></div>)}
              <button className={`cyber-button ${status}`} onClick={handleReview} disabled={status === "TRANSCRIBING..." || !mediaBlobUrl}>
                {status === "IDLE" && "ANALYZE VOICE DATA"}
                {status === "REVIEW" && "REVIEWING..."}
                {status === "TRANSCRIBING..." && "ðŸ§  TRANSCRIBING..."}
                {status.includes("Saved") && status}
                {status.includes("Received") && "âœ… SENT!"} 
                {status.includes("ERROR") && "âŒ RETRY"}
              </button>
            </>
          )}

          {selectedDialect !== "ADD_NEW" && (
            <div style={{marginTop: '10px', textAlign: 'center'}}>
               <button onClick={handleGeneratePersona} style={{background: 'none', border: '1px dashed #64748b', color: '#64748b', fontSize: '10px', padding: '5px 10px', borderRadius: '5px', cursor: 'pointer'}}>
                 âš™ï¸ ADMIN: GENERATE "{selectedDialect.toUpperCase()}" JSON
               </button>
            </div>
          )}
          <div className="id-footer">OPERATOR ID: {address.slice(0,6)}... | {consentStatus === "offline" ? "ðŸš« OFFLINE MODE" : "ðŸŸ¢ CONTRIBUTING"}</div>
        </div>

        {/* --- CAPTCHA MODAL --- */}
        {showCaptcha && (
            <div style={{position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0,0,0,0.85)', zIndex: 999, display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
                <div style={{background: '#1e293b', padding: '30px', borderRadius: '20px', border: '2px solid #3b82f6', textAlign: 'center', maxWidth: '300px'}}>
                    <div style={{fontSize: '40px', marginBottom: '10px'}}>ðŸ¤–</div>
                    <h3 style={{color: 'white', marginBottom: '10px'}}>Human Verification</h3>
                    <p style={{color: '#94a3b8', fontSize: '14px', marginBottom: '20px'}}>To create a new dialect, please say these numbers:</p>
                    <div style={{fontSize: '32px', fontWeight: 'bold', color: '#60a5fa', letterSpacing: '5px', marginBottom: '25px', background: 'rgba(0,0,0,0.3)', padding: '10px', borderRadius: '10px'}}>{captchaCode}</div>
                    
                    <div className="record-area" style={{marginBottom: '20px'}}>
                        {recStatus === "recording" ? (
                            <button className="record-btn recording" onMouseUp={stopRecording} onTouchEnd={stopRecording}><div className="pulse-ring"></div>ðŸŽ™ï¸ RELEASE</button>
                        ) : (
                            <button className="record-btn" onMouseDown={startRecording} onTouchStart={startRecording}>ðŸŽ™ï¸ HOLD TO SPEAK</button>
                        )}
                    </div>
                    {mediaBlobUrl && captchaStatus !== "VERIFYING" && (
                        <button onClick={verifyCaptcha} className="cyber-button SUCCESS" style={{width: '100%', marginBottom: '10px'}}>âœ… VERIFY AUDIO</button>
                    )}
                    {captchaStatus === "VERIFYING" && <div style={{color:'#fcd34d'}}>Checking...</div>}
                    {captchaStatus === "FAILED" && <div style={{color:'#ef4444', marginBottom:'10px'}}>âŒ Incorrect. Try again.</div>}
                    {captchaStatus === "SUCCESS" && <div style={{color:'#10b981', fontWeight:'bold'}}>âœ… VERIFIED!</div>}
                    <button onClick={() => { setShowCaptcha(false); setCaptchaStatus("IDLE"); }} style={{background:'none', border:'none', color:'#64748b', marginTop:'15px', cursor:'pointer'}}>Cancel</button>
                </div>
            </div>
        )}

      </div>
    </div>
  );
}

export default App;
